mapper.update:
update target_university tu set 
tu.target_university_name= #{targetUniversityName}, 
tu.target_university_lesson= #{targetUniversityLesson}, 
tu.target_university_rangking= #{targetUniversityRangking} 
WHERE tu.target_university_code = #{targetUniverseCode};

mapper.select:
SELECT
learning_schedule, user_code, sub_mjr_cat_code, sub_mir_cat_code, learning_schedule_memo, learning_schedule_days, learning_schedule_start, learning_schedule_end, learning_schedule_compleate
FROM learning_schedule;
