mapper.select:
SELECT exam_code, ei.user_code, u.user_name, u.user_id, exam_nm, exam_type, exam_oe_type, exam_tar_grd, exam_description, exam_date, exam_inst FROM exam_info ei INNER JOIN `user` u ON ei.user_code=u.user_code
u.grade_code ='ugc1'
AND exam_type = #{examInfo.examType}
AND exam_tar_grd = #{examInfo.examTarGrd}
AND exam_oe_type = #{examInfo.examOeType}
AND u.user_code = #{examInfo.userCode}
AND exam_date >= #{startDate}
AND exam_date <= #{endDate}
AND ${searchType} LIKE CONCAT('%',#{searchKeyword},'%')

mapper.update:
UPDATE qna_bank
SET
qna_cur_exam_state=#{qnaState}
WHERE
qna_code IN

	#{code}

mapper.select:
SELECT qb.qna_code, qb.user_code, u.user_id, u.user_name, qb.sub_mjr_cat_code, smjc.sub_mjr_cat_nm, qb.sub_mir_cat_code, smic.sub_mir_cat_nm, qb.qna_type, qna_chap, qb.qna_subject, qb.qna_qst, qb.qna_slt1, qb.qna_slt2, qb.qna_slt3, qb.qna_slt4, qb.qna_slt5, qb.qna_ans, qb.qna_scr, qb.qna_cmt, qb.qna_cur_exam_state, qb.qna_create_time, qb.qna_update_time FROM qna_bank qb LEFT JOIN sub_mjr_cat smjc ON qb.sub_mjr_cat_code = smjc.sub_mjr_cat_code LEFT JOIN sub_mir_cat smic ON qb.sub_mir_cat_code = smic.sub_mir_cat_code LEFT JOIN user u ON qb.user_code = u.user_code
${searchType} LIKE CONCAT('%', #{searchKeyword}, '%')
AND qb.qna_cur_exam_state = #{qnaCurExamState}
AND qb.user_code = #{userCode}
AND qna_scr = #{qnaScr}
AND qb.sub_mir_cat_code = #{subMirCatCode}
AND qb.sub_mjr_cat_code IN (SELECT SUBSTRING_INDEX(#{subMjrCatCode}, ',', 1) UNION SELECT SUBSTRING_INDEX(#{subMjrCatCode}, ',', 2) UNION SELECT SUBSTRING_INDEX(#{subMjrCatCode}, ',', -1) )
ORDER BY CAST(SUBSTRING(qna_code, 3) AS UNSIGNED) DESC
