mapper.insert:
/* 해당테이블에서 pk에서 가장큰 숫자를 알아오는 코드 */ SELECT CASE WHEN COUNT(*) = 0 THEN 1 ELSE MAX(CAST(SUBSTRING_INDEX(${pkColumnName}, #{preCode}, -1) AS UNSIGNED))+1 END AS maxNumber FROM ${tableName};
INSERT INTO ${tableName} VALUES
( CONCAT(#{preCode}, (#{idx}+#{maxNumber})), #{userCode}, (SELECT sub_mjr_cat_code FROM qna_bank WHERE qna_code = #{qna}), (SELECT sub_mir_cat_code FROM qna_bank WHERE qna_code = #{qna}), #{examCode}, #{qna}, #{userAnswer[${idx}]}, (SELECT qna_scr FROM qna_bank WHERE qna_code = #{qna}), NOW(), 1 )

mapper.select:
SELECT emq.exam_mapping_question_code, emq.exam_code, emq.exam_qna_code, qb.sub_mjr_cat_code, smirc.sub_mir_cat_nm, qb.sub_mir_cat_code, eqt.exam_qna_type_nm, qb.qna_type, eqc.exam_qna_chap_nm, qb.qna_chap, qb.qna_subject, qb.qna_qst, qb.qna_slt1, qb.qna_slt2, qb.qna_slt3, qb.qna_slt4, qb.qna_slt5, qb.qna_ans, qb.qna_scr, qb.qna_cmt, qb.qna_create_time FROM exam_mapping_question emq INNER JOIN qna_bank qb ON emq.exam_qna_code = qb.qna_code INNER JOIN sub_mir_cat smirc ON qb.sub_mir_cat_code = smirc.sub_mir_cat_code INNER JOIN exam_qna_chap eqc ON qb.qna_chap = eqc.exam_qna_chap_code INNER JOIN exam_qna_type eqt ON qb.qna_type = eqt.exam_qna_type_code WHERE emq.exam_code=#{examCode}
AND qb.sub_mir_cat_code=#{currentSubject}
AND qb.sub_mjr_cat_code=#{currentMjrCode}
ORDER BY CAST(SUBSTRING(exam_mapping_question_code,5) AS UNSIGNED)

SELECT ei.exam_code, ei.user_code, ei.exam_nm, ei.exam_type, ei.exam_oe_type, ei.exam_tar_grd, ei.exam_description, ei.exam_date, ei.exam_inst, COUNT(emq.exam_qna_code) AS registeredQuestionCount, GROUP_CONCAT(DISTINCT qb.sub_mjr_cat_code ORDER BY qb.sub_mjr_cat_code SEPARATOR ', ') AS newCode FROM exam_info ei LEFT JOIN exam_mapping_question emq ON ei.exam_code = emq.exam_code INNER JOIN qna_bank qb ON emq.exam_qna_code = qb.qna_code
GROUP BY ei.exam_code
HAVING registeredQuestionCount > 0 AND ei.exam_type != '자체'
AND YEAR(ei.exam_date) BETWEEN #{beginYear} AND #{endYear}
AND ei.exam_type = #{examType}
AND ei.exam_tar_grd = #{examTarGrd}
AND MONTH(ei.exam_date) IN
#{m}
AND newCode LIKE concat('%', #{s},'%')
