mapper.update:
update target_university


		target_university_name= #{targetUniversityName},


		target_university_lesson= #{targetUniversityLesson},


		target_university_rangking= #{targetUniversityRangking}


WHERE
target_university_code = #{targetUniverseCode};

mapper.select:
SELECT
learning_schedule, user_code, sub_mjr_cat_code, sub_mir_cat_code, learning_schedule_memo, learning_schedule_days, learning_schedule_start, learning_schedule_end, learning_schedule_compleate
FROM learning_schedule where user_code = #{id};

mapper.insert:
SELECT CASE WHEN COUNT(*) = 0 THEN CONCAT('dsc', 1) ELSE CONCAT('dsc', MAX(CAST(SUBSTRING_INDEX(detailed_schedule_code, 'dsc', -1) AS UNSIGNED))+1) END AS detailedSchedule FROM detailed_schedule;
insert into detailed_schedule VALUES( #{detailedSchedule}, #{userCode},
NULL
#{learningSchedule}
, #{smjrcc}, #{smircc}, #{detailedScheduleMemo}, #{detailedScheduleDate}, #{detailedScheduleStartTime}, #{detailedScheduleEndTime}, TIME_FORMAT(TIMEDIFF(#{detailedScheduleEndTime}, #{detailedScheduleStartTime}), '%H:%i'), #{detailedScheduleResult} );
